.vpot {
  --led-height: 3rem;
  --led-width: 10%;
}

.vpot canvas {
  opacity: 0;
}
.vpot .html {
  position:absolute;
  height: 100%;
  width: 100%;
  top:0:
  left:0;
}
.vpot .knob {
  position: absolute;
  left:50%;
  top: 50%;
  width: 24rem;
  height: 24rem;
  margin-left: -12rem;
  margin-top: -12rem;
  border-radius: 50%;
  background: var(--color-widget);
  opacity: .75
}
.vpot .led {
  position: absolute;
  width: 100%;
  height: var(--led-height);
  top: 50%;
  margin-top: calc(-0.5 * var(--led-height));
}
.vpot .led:after, .led:before {
  content: "";
  position: absolute;
  width: var(--led-width);
  height: var(--led-height);
  left:5rem;top:0;
  background: red;
  border-radius: 50%;
  opacity: var(--led-opacity);
}
.vpot .led:before {
  background: var(--color-widget);
  opacity: 0.25;
}
.vpot .led-0 {
  left: 50%;
  top: 75%;
  width: 0;
  margin: 0;
}
.vpot .led-0:after, .vpot .led-0:before {
  width: 7rem;
  height: 7rem;
  left: -3rem;
}


